// frontend/src/pages/ResetPasswordPage.jsx

import React from "react";
import {
  Box,
  Stack,
  TextField,
  Typography,
  Button,
  useMediaQuery,
  useTheme,
} from "@mui/material";

const ResetPasswordPage = () => {
  const theme = useTheme();
  const isSmDown = useMediaQuery(theme.breakpoints.down("sm"));

  return (
    <Box
      dir="rtl"
      sx={{
        width: isSmDown ? "100vw" : "536px",
        height: "100vh",
        backgroundColor: "#f5f5f5",
        fontFamily: "Cairo, sans-serif",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        px: isSmDown ? 2 : 0,
      }}
    >
      {/* Logo and Heading */}
      <Stack
        width={isSmDown ? "160px" : "250px"}
        alignItems="center"
        spacing={1.5}
        mb={2}
      >
        <img
          src="/images/logo.png"
          alt="Tadrab Logo"
          style={{
            width: isSmDown ? "120px" : "250px",
            height: isSmDown ? "120px" : "250px",
          }}
        />
        <Typography
          fontSize={isSmDown ? "22px" : "28px"}
          fontWeight={800}
          color="#000"
          textAlign="center"
          sx={{ fontFamily: "Cairo" }}
        >
          تعيين كلمة مرور جديدة
        </Typography>
        <Typography
          fontSize="14px"
          color="#888"
          textAlign="center"
          sx={{ fontFamily: "Cairo" }}
        >
          يجب أن تكون 8 أحرف على الأقل
        </Typography>
      </Stack>

      {/* Form Section */}
      <Stack spacing={isSmDown ? 2 : 4} width="100%">
        {/* Password Field */}
        <Stack spacing={1}>
          <Typography
            color="#000000cc"
            sx={{ fontFamily: "Cairo", fontSize: isSmDown ? "14px" : "inherit" }}
          >
            كلمة المرور:
          </Typography>
          <TextField
            variant="filled"
            placeholder="أدخل كلمة المرور"
            fullWidth
            InputProps={{
              disableUnderline: true,
              style: {
                textAlign: "center",
                padding: isSmDown ? "8px 8px" : "10px 16px",
                height: isSmDown ? "40px" : "48px",
                backgroundColor: "#ECF1F6",
                borderRadius: "15px",
                fontFamily: "Cairo",
                fontSize: isSmDown ? "14px" : "16px",
                fontWeight: 500,
              },
            }}
          />
        </Stack>

        {/* Confirm Password Field */}
        <Stack spacing={1}>
          <Typography
            color="#000000cc"
            sx={{ fontFamily: "Cairo", fontSize: isSmDown ? "14px" : "inherit" }}
          >
            تأكيد كلمة المرور:
          </Typography>
          <TextField
            variant="filled"
            placeholder="أدخل كلمة مرور جديدة"
            fullWidth
            InputProps={{
              disableUnderline: true,
              style: {
                textAlign: "center",
                padding: isSmDown ? "8px 8px" : "10px 16px",
                height: isSmDown ? "40px" : "48px",
                backgroundColor: "#ECF1F6",
                borderRadius: "15px",
                fontFamily: "Cairo",
                fontSize: isSmDown ? "14px" : "16px",
                fontWeight: 500,
              },
            }}
          />
        </Stack>

        {/* Submit Button */}
        <Button
          variant="contained"
          fullWidth
          sx={{
            backgroundColor: "#317B74",
            borderRadius: "15px",
            height: isSmDown ? "40px" : "48px",
            fontSize: isSmDown ? "14px" : "16px",
            fontWeight: 600,
            textAlign: "center",
            fontFamily: "Cairo",
            "&:hover": { backgroundColor: "#2d6e69" },
          }}
        >
          إعادة تعيين كلمة المرور
        </Button>
      </Stack>
    </Box>
  );
};

export default ResetPasswordPage;
